/*!50003 CREATE DEFINER=`emplopad`@`%` FUNCTION `getholiday`(dateid DATE, userid INT(11)) RETURNS varchar(8) CHARSET utf8
BEGIN
    SET @daytypes = 'REGULAR';
    SET @usercount = 0;
