/*!50003 CREATE DEFINER=`emplopad`@`%` FUNCTION `get_headcount_per_dept`( nDepartment INT(11), nCompanyid INT(11), nBranchid INT(11), sPayrolldate VARCHAR(255) ) RETURNS int(11)
BEGIN
	SET @nHeadCount= 0;
	SELECT COUNT(*) INTO @nHeadCount 
