DELIMITER $$

/*!50003 CREATE DEFINER=`emplopad`@`%` FUNCTION `time_form_obt_request`(formsid INT(11)) RETURNS mediumtext CHARSET utf8
BEGIN
    DECLARE done TINYINT(11) DEFAULT 0;
    DECLARE rec INT(11) DEFAULT 0;
    
    DECLARE recordid INT;
