              END; -- BEGIN
		        
		        
		END IF;	-- first done	  
		
		
	              
		      
	    UNTIL done END REPEAT;
	    
	    CLOSE users_emails;
	    
	    -- COMMIT;
	    SELECT rec;
     END IF;
     
    END */$$
