       INNER JOIN `users` u ON u.user_id=up.user_id AND u.`active`=1
       WHERE m.`deleted`=0 AND m.`memo_id`=memoid;
       
    DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;
    
    
    -- [1] retrieve the memo
    SET @memo_id = 0;
    SET @memo_type_id = 0;
    SET @memo_type = '';
    SET @memo_title = '';
    SET @attachment = '';
    SET @memo_body = '';
    SET @created_on = '';
    SET @fileattach = '';
       
