    SET @system_author = (SELECT `value` FROM `ww_config` WHERE `key`='author' LIMIT  1);
    SET @system_fromaddress = (SELECT `value` FROM `ww_config` WHERE `key`='from_address' LIMIT  1);
    
    SELECT `memo_type_id`, `memo_title`, 
