				   PREPARE stmt FROM @SQL;
				   EXECUTE stmt;
				   DEALLOCATE PREPARE stmt;
				ELSE BEGIN END;  
				END CASE;
			end if;
		END IF;
				
	     UNTIL done END REPEAT;
		    
	     CLOSE forms;
		    
	      -- COMMIT;	    
    END */$$
