	IFNULL(get_bir_total_taxable( nYear, nUserID ),0), 	
	IFNULL(get_bir_net_taxable( nYear, nUserID, t.`amount` ),0), 	
	IFNULL(get_wtax( nYear, nUserID ),0), 
	IFNULL(get_taxdue( nYear, nUserID, t.`amount` ),0), 
