DELIMITER $$

/*!50003 CREATE DEFINER=`emplopad`@`%` PROCEDURE `sp_payroll_get_ecola`(
	in puserid int(11), 
	in periodid int(11), 
	in paydate date, 
	in cola_amt decimal(12,2),
	in processing_type_id int(11)
    )
BEGIN
	SET @tran_code = '';
