			     @tran_type_id, 0,'',0,0,'',NOW(),0,0
			FROM ww_payroll_partners 
			WHERE user_id = puserid;
		END IF;
		
		IF @ec > 0 THEN
			SET @tran_code = '';
			SET @tran_id = 0;
			SET @tran_class_id = 0;
			SET @tran_type_id = 0;
			SELECT `transaction_code`, `transaction_id`, `transaction_type_id`, `transaction_class_id`
			INTO @tran_code, @tran_id, @tran_type_id, @tran_class_id
