    (
       CASE WHEN MONTH(datefrom)=12 THEN 
          ( MONTH(`up`.`birth_date`)=12 OR MONTH(`up`.`birth_date`) BETWEEN 1 AND MONTH(dateto) )
       ELSE 
          ( MONTH(`up`.`birth_date`) BETWEEN MONTH(datefrom) AND MONTH(dateto) )
