/*!50003 CREATE DEFINER=`emplopad`@`%` PROCEDURE `sp_time_calendar_manager_insert_weekly`(IN userid INT(11), IN dates DATE, IN calendarid INT(1))
BEGIN
    /* 2017-03-21 : Added checking of forms application
