	       WHERE a.`deleted`=0 AND b.`class_code`=@class AND a.`company_id`=@company;
	    END IF; -- IF COMPANY
            
         END IF; -- IF DEPARTMENT
         
      END IF; -- IF POSITION  
      
   END IF; -- IF USER  
   
    END */$$
