             END IF;
          ELSE
             -- DEFAULT
             CALL sp_time_form_balance_process(YEAR(CURDATE()), 0);
          END IF;
       END IF;
    
    UNTIL time_form_balance_process_credit END REPEAT;
    
    CLOSE balance_process_credit;
    
    END */$$
