    INSERT INTO `ww_system_sms_queue` (`to`,`subject`,`body`)
    SELECT 
       REPLACE(REPLACE(TRIM(pp.`key_value`),'-',''),'+',''), 
       IF(IFNULL(`nickname`,'')='',`firstname`,`nickname`),
       REPLACE(
       REPLACE(
       REPLACE(`body`, 
