insert into `ww_system_template` (`template_id`, `mod_id`, `code`, `name`, `subject`, `body`, `description`, `deleted`) values('125','0','movement-hr-approved','Movement Approved by HR','Movement Approved by HR','<html><head><meta charset=\"utf-8\"></head>\r\n<body style=\"background-color:#F9F9F9;padding-top: 18px;\">\r\n<div style=\"width: 90%; font-family: Verdana, Helvetica, Arial, sans-serif; font-size: 13px; font-weight: normal; line-height: 18px; color: #333; margin: 0 auto; background-color: #fff;border: 1px solid #DDD; border-radius: 4px; padding: 15px;\">\r\n    <p style=\"border-bottom:1px solid #eee; margin:0 0 30px 0; padding-bottom:15px;\"><img height=\"32px\" src=\"{{system_logo}}\" alt=\"Please enable images to load system logo\" title=\"Please enable images\"></p>\r\n\r\n    <p>Dear {{alias}},</p>\r\n\r\n        <p>\r\n            We would like to inform you that <strong>{{form}}</strong> filed by <strong>{{fullname}}</strong> has been approved by HR.\r\n        </p>\r\n\r\n        <p>\r\n            To review this application, you may <a href=\"{{system_route}}\" target=\"_blank\">click here</a>.\r\n        </p>\r\n\r\n        <br />\r\n\r\n    <p>@Emplopad</p>\r\n\r\n    <p style=\"font-size:x-small\">\r\n        Disclaimer! Please do not reply to this message; it was sent from an unmonitored email address. This message is a service email related to your use of Emplopad.\r\n        <br>\r\n        Powered by Teemworx\r\n    </p>\r\n</div>\r\n</body>\r\n</html>',NULL,'0');
insert into `ww_system_template` (`template_id`, `mod_id`, `code`, `name`, `subject`, `body`, `description`, `deleted`) values('126','0','RELEASE-QUITCLAIM','Quitclaim','Quitclaim','<html><head><meta charset=\"utf-8\"></head>\r\n<body>\r\n<div style=\"font-family: Verdana, Helvetica, Arial, sans-serif; font-size: 13px; font-weight: normal; line-height: 18px; color: #333;\">\r\n    <div class=\"clearfix\"><br/></div>\r\n	<div class=\"clearfix\"><br/></div>\r\n	\r\n	<p style=\"margin:0 0 30px 0;\"><img src=\"{{logo}}\" alt=\"Please enable images to load system logo\" title=\"Please enable images\"></p>\r\n		\r\n	<div class=\"clearfix\"><br/></div>\r\n	<div class=\"clearfix\"><br/></div>\r\n	\r\n	<h1 style=\"text-align:center\"><u>RECEIPT & QUITCLAIM</u></h1>\r\n	\r\n	<div class=\"clearfix\"><br/></div>\r\n\r\n	<h3>KNOW ALL MEN BY THESE PRESENTS:</h3>\r\n\r\n	<br />\r\n\r\n	<p>I, <u>{{gender}} {{employee_name}}</u>, of legal age, single/married and presently residing at <u>{{address}}</u> under oath, hereby confirm and acknowledge that my employment with {{company}} ceased effective {{resigned_date}} by virtue of my voluntary resignation therefrom.</p>\r\n	\r\n	<p>In consideration of the sum _______________________________________________(&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;), receipt of which I acknowledge, I hereby forever release <u>{{company}}</u> and its officers, directors, representatives, agents and I herreby irrevocably waive, any and all claims and causes of action judgement of any tribunal I have or may have in my favor againts them now or in the future, arrising from my employment and cessation of such employment with <u>{{company}}.</u></p>\r\n\r\n	<p>I also acknowledge having been paid all emoluments and compensation that I may be entitled to under law, contract or company policy and that this quitclaim is final statement of all my claims, disclosed or otherwise, which quitclaim should not be deemed as admission of any liability for all intents and purposes by <u>{{company}}</u> and its officers, directors, representatives, agents and assigns.</p>\r\n\r\n    <p>I have executed this quitclaim voluntarily and without mental reservations and only after honest and full reflection on all the incidents involving <u>{{company}}</u>.For this reason, I bind myself to defend the voluntariness and the truthfullness of this quitclaim before any forum and under any circumtances in which my personal participation may be needed.</p>\r\n\r\n	<div class=\"clearfix\"><br/></div>\r\n	<div class=\"clearfix\"><br/></div>\r\n\r\n	<p>________________________20_________                     _____________________________________________________</p>\r\n	<h2><center>WITNESS</center></h2>\r\n	<p>___________________________________                     _____________________________________________________</p>\r\n\r\n	<p>&nbsp;&nbsp;&nbsp;&nbsp;<b>SUBSCRIBED AND SWORN</b> to before me this <u>{{day}}</u> day of <u>{{date_month}}</u>, 20 <u>{{year}}</u>, affiant having exhibited to me his/her Residence Certificate No. _______________________ issued at _____________________ on ______________________________</p>\r\n\r\n\r\n	<p>Doc. No.  ________________________</p>\r\n	<p>Page. No. ________________________</p>\r\n	<p>Book. No. ________________________</p>\r\n	<p>Series of ________________________</p>\r\n</div>\r\n</body>\r\n</html>\r\n',NULL,'0');
insert into `ww_system_template` (`template_id`, `mod_id`, `code`, `name`, `subject`, `body`, `description`, `deleted`) values('127','0','CLEARANCE-FORM-EXIT','Exit Interview','Exit Interview','<html><head><meta charset=\"utf-8\"></head>\r\n<body>\r\n<div style=\"font-family: Verdana, Helvetica, Arial, sans-serif; font-size: 13px; font-weight: normal; line-height: 18px; color: #333;\">\r\n    <div class=\"clearfix\"><br/></div>\r\n	<div class=\"clearfix\"><br/></div>\r\n	\r\n	<p style=\"margin:0 0 30px 0;\"><img src=\"{{logo}}\" alt=\"Please enable images to load system logo\" title=\"Please enable images\"></p>		\r\n	\r\n	<h2 align=\"center\">Armadillo Holdings Inc. <br /> Exit Interview From</h2>\r\n	<p style=\"padding:0;margin:0\">Ikinalulungkot namin ang iyong pag-alis sa AHI. ngunit bago ang lahat, nais sana naming ipakausap ang iyong pagsagot sa <i>questionaire</i> na ito. Ang iyong mga kasagutan ay maaaring makatulong para mapabuti ang pamamalakad ng kumpanya.</p>\r\n	<p style=\"padding:0;margin:0\">Maasahan mong ituturing naming <i>confidential</i> ang iyong mga isusulat. Ipagdarasal namin ang iyong tagumpay sa bago mong adhikain.</p>\r\n	<br />\r\n	<table width=\"100%\" cellspacing=\"0\" style=\"border-collapse: collapse;border-spacing: 0; font-size: 12px;\">\r\n		<tr>\r\n			<td width=\"15%\">Name:</td>\r\n			<td width=\"65%\" style=\"border-bottom:1px solid black;font-size: 10px;\">{{employee_name}}</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"15%\">Locale:</td>\r\n			<td width=\"6%\" style=\"border-bottom:1px solid black;font-size: 10px;\">{{department}}</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"15%\">Position:</td>\r\n			<td width=\"65%\" style=\"border-bottom:1px solid black;font-size: 10px;\">{{position}}</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"15%\">Effective Date of Resignation:</td>\r\n			<td width=\"65%\" style=\"border-bottom:1px solid black;font-size: 10px;\">{{resigned_date}}</td>\r\n		</tr>		\r\n	</table>\r\n\r\n	<br />\r\n\r\n	{{exit_interview_body}}\r\n	\r\n	<br />\r\n\r\n	<table width=\"100%\" cellspacing=\"0\" style=\"border-collapse: collapse;border-spacing: 0; font-size: 12px;\">\r\n		<tr>\r\n			<td width=\"25%\" style=\"border-bottom:1px solid black\">&nbsp;</td>\r\n			<td width=\"25%\">&nbsp;</td>\r\n			<td width=\"25%\">&nbsp;</td>\r\n			<td width=\"25%\">Exit Interview Conducted By:</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">Employee\'s Signature / Date</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td style=\"border-bottom:1px solid black\"></td>\r\n		</tr>		\r\n	</table>\r\n\r\n</div>\r\n</body>\r\n</html>\r\n',NULL,'0');
